CREATE DATABASE ucheb_5;

USE ucheb_5;

CREATE TABLE `auth` (
  `auth_id` int PRIMARY KEY AUTO_INCREMENT,
  `auth_log` varchar(20),
  `auth_pwd` text,
  `auth_role` int
);

CREATE TABLE `rols` (
  `role_id` int PRIMARY KEY AUTO_INCREMENT,
  `role_name` varchar(20)
);

CREATE TABLE `items` (
  `item_id` int PRIMARY KEY AUTO_INCREMENT,
  `item_name` varchar(50),
  `item_desc` text,
  `item_cost` double
);

CREATE TABLE `cort` (
  `order_id` int PRIMARY KEY AUTO_INCREMENT,
  `item_id` int,
  `amount` int
);

ALTER TABLE `cort` ADD FOREIGN KEY (`item_id`) REFERENCES `items` (`item_id`);

ALTER TABLE `auth` ADD FOREIGN KEY (`auth_role`) REFERENCES `rols` (`role_id`);

INSERT INTO `rols` VALUES (1,'Администратор'),(2,'Пользователь');

INSERT INTO `auth` VALUES (1,'admin','admin',1),(2,'222','222',2),(3,'333','333',2),(5,'444','444',2);

INSERT INTO `items` VALUES (2,'Спортивный футбольный мяч Адидас Uniforia 5 размер','Uniforia это футбольный мяч разработанный компанией Адидас специально для игра чемпионата Европы 2020 года. Презентация состоялась 6 ноября 2019 года. Название объединяет два понятия unity единство и euphoria эйфория. Накладки скреплены пеной, это улучшает аэродинамические показатели снаряда.Материалы:1. Камера- Бутил2. Внешний вид - Полиуретан 3. Кол-во подкладочных слоев- 3шт.4. Сшивка- Термосклейка. Мяч футбольный по размеру одинаковые как баскетбольный мяч, волейбольный мяч. Футбольный мяч высокого класса, который обладает отличными характеристиками и подходит для проведения игр профессионального уровня, Мяч Адидас Унифория подходит для тренировок профессиональных команд, для игр в зале, для любительских игр как для взрослых, так и для детей. Очень похож на мячей Demix. Будет отличным подарком на 23 февраля, подарок для мужа, подарок для брата или подарок для сына. Если закажете будет поставляться в течений 1-2 дня. Наш магазин очень рекомендует для вас этот мяч. Вес Мяча всего лишь 450 г.',1800),(3,'Мяч волейбольный Mikasa V300W 5 размер желто-синий','Волейбольный мяч Mikasa наделен совершенным балансом, благодаря схеме расположения панелей покрышки. Увеличенное сопротивление воздуху (аналогично поведению мяча для гольфа), скорость мяча немного снижается, и приводит к полетным характеристикам очень похожим на V200W. Покрышка мяча выполнена из Microfiber + PU, материал разработан совместно Mikasa и Kuraray. Для минимизации сопротивления воздуха и максимальной силы сцепления поверхности шара применяется технология SINGLE DIMPLE. Также данная модель отличается усовершенствованной технологией склейки. Мяч впитывает влагу, не скользит и имеет идеальную сферическую форму. Мяч волейбольный Mikasa V300W FIVB NEW одобрен для проведения международных и национальных соревнований. Окружность мяча 65-67 см, вес от 260 до 280 грамм. ВНИМАНИЕ! Все мячи с завода-изготовителя поступают в спущенном или приспущенном состоянии для их безопасного перемещения и хранения. Перед использованием необходимо накачать мяч до рекомендуемого производителем давления.Родина бренда: Япония',6899),(4,'Мяч волейбольный спорт. пляж. mikasa molten раз. 5','Мяч волейбольный Mikasa №5 V200W, V300W, V330W и Molten №5 V5VC, V5M1500 отлично подойдет для тренировок начинающим спортсменам и любительских соревнований. Спортивный мяч полностью соответствует официальным параметрам FIVB (Международная федерация волейбола). Покрышка мяча изготовлена из премиальной синтетической кожи, полиуретана, 18 клееных панелей сине-желтого цвета или бело-красно-зеленого цвета (материал и цвет зависит от модели мяча). Камера из бутила обеспечивает долговечность и правильное распределение давления по всей поверхности мяча. Технология Simple Dimple – двойные углубления которые улучшают сцепление с пальцами. Технология Nano Balloon Silica – снижает риск проскальзывания мокрых рук. Технология Sensi Touch - \"осязаемое прикосновение\", которые улучшают сцепление с пальцами (технологии зависят от модели мяча). Нейлоновый средний слой выполнен из эластичных нейлоновых нитей, что делает мяч прочным и гарантирует абсолютную сферичность. Волейбольный мяч тренировочный Микаса и Молтен подойдет для любительских и профессиональных спортивных игр, тренировок, классического и пляжного волейбола, также для игр на улице, природе, даче, отдыха, взрослых и детей. Мяч поставляется в сдутом виде. Перед использованием необходимо надуть насосом с иглой до необходимой упругости. Страна производства: Таиланд. Комплектация: мяч волейбольный -1 шт, насос с иглой и гибким шлангом -1 шт.',2288),(5,'Мяч спортивный Demix ','Баскетбольный мяч 5 размера с классической 8-панельной конструкцией подойдет для детских тренировок.',479),(6,'Мяч баскетбольный Molten для зала и улицы разм. 7','Мяч баскетбольный спортивный. Размер 6, 7 Molten GG6X, GG7X идельно подходит для игры в спортзале, зале, улице, спортплощадке. Применение: для тренировочных игр и соревнованиях на любых поверхностях. Подойдет для мини-баскетбола, женского и мужского баскетбола, также для детей, для игры на свежем воздухе. Мяч изготовлен из высокопрочного, современного, композитного материала, который обеспечивает качественный захват мяча и дает превосходное чувство мяча и делает его долговечным. Бутиловая камера мяча не пропускает воздух, позволяет поддерживать давление без подкачивания, форма мяча остается не изменой за счет армирования камеры нейлоновой нитью Официальный размер и вес FIBA, профессиональный, тренировочный, для любительских встреч и игр. Рекомендованные покрытия: резина, асфальт, бетон, паркет. Мяч с фабрики идет в спущенном состоянии, в подарок в комплекте игла для накачивания. Перед накаливанием намочите иглу, чтобы не повредить ниппель. Подходит для подарка папе, мужу, жене, парню, девушке, ребенку, сыну, дочке, коллеге, спортсмену. На такие праздники. как: 14 февраля день всех влюбленных, 23 февраля, 8 марта.',2391),(7,'Беговая дорожка UNIX Fit Wood Way','Беговая дорожка серии Wood Way от бренда UNIX FIT - это надежный и компактный тренажер для домашнего использования с уникальным дизайном в скандинавском стиле. Боковые элементы из массива дуба - отличный выбор для людей с хорошим вкусом и ценителей высококачественного экологичного материала. Это идеальный вариант для тех, кто хочет повысить свою выносливость, сбросить лишний вес и поддерживать в тонусе сердечно-сосудистую систему. На модели Wood Way установлен бесшумный двигатель мощностью 1,25 см, который подходит для ходьбы или легкого бега, и разгоняет полотно до 8 км/ч. Минимальная скорость составляет всего 0,5 км/ч. Антискользящее беговое полотно размером 110 х 42 см и система амортизации обеспечивают безопасность тренировки и снижают нагрузку на позвоночник и суставы. Дорожка поставляется в собранном виде и готова к тренировке сразу после покупки. Удобный пульт ДУ позволяет выбрать спортивный режим тренировки или выбрать одну из 12 встроенных программ. Дополнительно есть возможность провести целевые тренировки на время, расстояние и калории. Контролировать параметры тренировки помогает большой LED-дисплей размером 14 см, отображает скорость тренировки, пройденное расстояние, время и количество сожженных калорий. Компактные габариты и высота всего 12 см - это возможность сэкономить ваше пространство. После тренировки тренажер легко убрать под диван или кровать с помощью транспортировочных колес. Вес дорожки всего 20 кг.',26999),(8,'Электросамокат MINIPRO mi-668 ','Электросамокат MiniPro Mi668 - синоним слову комфорт. Minipro Mi668 - электро самокат, в котором есть все необходимое и даже больше: два удобных сиденья, скорость до 30 км/ч и запас хода до 35 километров. В нем есть все для удобных поездок по городу, и даже больше! Надувные резиновые колеса смягчают неровности, изогнутый руль не даст рукам устать, а два сиденья делают электросамокат по-настоящему семейным! Если вы ищите транспорт для совместных прогулок или поездок в детский сад, то электросамокат с двумя сиденьями - идеальное решение. Модель заводится ключом и легко выдерживает до 120 кг нагрузки. Этот электросамокат имеет одну из самых надежных систем торможения - дисковую. Благодаря ей вы всегда остановитесь быстро и плавно, вне зависимости от скорости передвижения. Это ваша безопасность и супер хорошая проходимость на любых дорогах. Чтобы ход Minipro Mi668 был мягким, производители наделили самокат 12-дюймовыми колесами с надувными покрышками. Они сгладят всю ненужную тряску и вибрацию при встрече с неровностями. Самокат идеально подходит для поездок по городу, паркам и аллеям. Несмотря на свою семейность, электросамокат подойдет и опытным райдерам. Если вы любите как следует погонять вечерком, то вы должны знать, что в заднее колесо самоката встроен двигатель на 350 Вт. С его помощью на Minipro Mi668 вы разгонитесь до 30 км/ч всего за 7 секунд и преодолеете небольшие подъемы в гору. Он очень устойчивый на поворотах: на нем можно легко выполнять трюки и практиковать экстремальную езду. Скорость можно регулировать тормозной системой, о которой мы проговорили ранее. Дисковая система, которая установлена с обеих сторон, поэтому быстро реагирует на нажатие. Стоит слегка потянуть рычаг на себя, и вы плавно остановитесь. Со встроенным аккумулятором, емкостью 10 Ач, вы проедете до 35 километров пути. Батарея спрятана в железной колбе под сиденьем. Дальность хода зависит от нагрузки на электросамокат и качества дорожного покрытия. Чтобы полностью восстановить мощность такой батареи понадобится 4-6 часов. Чтобы защитить аккумулятор от пыли и влаги, мы рекомендуем установить влагозащиту. Так электросамокат будет готов к любой погоде и на нем можно будет ездить даже по глубоким лужам.Еще один плюс электросамоката в легком управлении. Вам не понадобится часами сидеть с инструкцией, чтобы понять, как все работает. Чтобы поехать, достаточно завести Mi668 ключом и нажать кнопку газа. Никаких заморочек с режимами и переключателями! Материал рамы - авиационный алюминий. Благодаря этому типу металла, электросамокат Minipro Mi668 весит 19 килограмм при грузоподъемности в 120. Самокат обладает компактными размерами, и его можно хранить дома. Главное условие - сухое и теплое помещение, без попадания прямых солнечных лучей. Два сиденья модели обшиты мягким и удобным материалом. Дополнительные плюсы, это передняя фара для безопасных поездок в темное время суток, подножка для парковки, подставки для ног второго пассажира, стоп-сигнал.Уже сделали свой выбор? Если да, то купить Minipro Mi668 вы можете у у нас, не сомневаясь в целостности и укомплектованности. Желаем Вам хороших покупок и отличного настроения.',29686),(11,'Электросамокат Ninebot KickScooter F20A','KickScooter F20A – это базовая модель из обновлённой линейки электросамокатов от Ninebot. Среди отличительных особенностей: запас хода до 20 километров, компактный аккумулятор, вентилируемый дисковый тормоз, устойчивые к проколам покрышки и прочная стальная рама, выдерживающая нагрузку до 100 килограмм',42840),(12,'Коньки хоккейные Powerslide','Управляйте хоккейной площадкой в коньках Nemesis от POWERSLIDE. \r\nНезависимо от того, новичок вы или опытный игрок, Nemesis предоставит вам все необходимое, чтобы вывести вашу игру в хоккей на новый уровень. \r\nБотинок может выдержать удар и выйти на первое место. Прочный и поддерживающий ботинок изготовлен из износостойкого баллистического нейлона и имеет прочный внутренник из полиэтилена. Вощеные шнурки вместе с прочными проушинами и металлической петлей под углом 45° обеспечивают хоккеистам идеальное сочетание поддержки и гибкости. \r\nВ комплекте с нейлоновым композитным держателем лезвия и лезвием из нержавеющей стали 303 хоккейные коньки POWERSLIDE Nemesis быстрые, стабильные, поддерживающие и готовые к игре.',6926),(13,'Теннисный стол Hobby Evo синий модерн','Модель Evo относится к новому поколению легендарной линейки Hobby, которая заслуженно пользуется широкой популярностью среди любителей настольного тенниса.Hobby Evo blue - любительский теннисный стол для дома и офиса. Модель облегченного конструктива и ультрасовременного дизайна. Ноги стола специальной формы,  с характерным изгибом. Раскосы ярко-красного цвета с надежной фиксацией обеспечивают устойчивость конструкции в разложенном виде и во время игры. Модель легко устанавливается и очень компактна при хранении.\r\nЦвет:синий\r\nИгровое поле:ЛДСП 16 мм с меламиновым покрытием (пр-во: Россия-Германия).\r\nСетка:в комплект НЕ ВХОДИТ\r\nНоги:стальная труба 25 мм с полимерным покрытием.\r\nРазмер стола:длина 273 см, ширина 152,5 см, высота 75 см.\r\nРазмер в сложенном виде:длина 152,5 см, ширина 8 см, высота 137 см.\r\nРазмер упаковки:длина 157 см, ширина 9 см, высота 141 см.\r\nСистема хранения: :складные раскосы ног.\r\nВес стола:54 кг.\r\nВес в упаковке:60 кг.\r\nКласс изделия:3\r\nКант:кромка ПВХ 0,45 мм.\r\nАртикул:6016-3',10199),(14,'Палатка семиместная Black Turtle домик (7 мест)','Палатка туристическая Black Turtle – идеальная палатка для семейного отдыха с детьми, с друзьями , походная , для рыбалки. Палатка туристическая 4 , 5 , 6 , 7 или 8 местная – двухкомнатная , большая и просторная.\r\nПалатка двухслойная – отличный вариант для отдыха большой компанией на природе, сделана из качественных материалов: премиальный тент из многослойного покрытия, легкие и прочные соединительные дуги. Шатер палатка оснащена тремя входами и тремя окнами, с закрывающимися шторами и москитными сетками высокой плотности, что обеспечит комфортный отдых без проникновения лучей солнца и насекомых. Палатка домик оснащена верхним съемным тентом, который обеспечивает дополнительный приток воздуха и не дает попадать осадкам внутрь палатки.\r\nОтличительной чертой палатки с тамбуром является козырек, под которым комфортно разместится 4 человека во время дождя или палящего солнца. Козырек крепится на двух штырях с применением растяжек для устойчивости.\r\nЭргономика палатки так же имеет преимущества в виде внутреннего кармана для документов и телефонов, крепления для фонаря, внутренней перегородки, которая делит большую комнату на 2 спальни. Палатка кемпинговая имеет высоту 200 см и штормовые растяжки, которые позволяют комфортно отдыхать в непогоду.\r\nВес палатки 15 кг и удобный чехол позволяют брать палатку в любое путешествие: отдых на море, поход, рыбалка, охота, семейный кемпинг и отдых на даче.\r\nПалатка летняя обладает хорошими показателями влагостойкости благодаря качественным непромокаемым материалам и проклеенным швам, что сделает Ваш поход и отдых на природе комфортным в любую погоду. \r\nВнешние размеры палатки 380*260*200 см. Палатка рыболовная имеет плотность ткани 210Т и влагостойкость тента 2000 мм. Палатка универсальная имеет размеры в сложенном виде 70*28*26 см.\r\nТуристическая палатка четырехместная , пятиместная, шестиместная, семиместная, восьмиместная.\r\nПалатку для кемпинга Вы можете приобрести в подарок мужу , мужчине , другу , коллеге , любимому , папе , дедушке, брату , родителям , родственникам , на день рождения , 14 февраля , 23 февраля или просто без повода.\r\n',21159),(15,'Эспандер ЮО Кистевой тренажер (1 шт.) 30 кг','Эспандер плечевой пружинный (5 пружин) - Многофункциональный эспандер 2 в 1 с пятью пружинами, включает в себя эспандер плечевой, эспандер для пресса и спины, эспандер кистевой пружинный. Развивает мышцы кистей, пальцев, рук, плеч, груди и спины. Предназначен для выполнения упражнений с целью развития мышц спины, верхнего плечевого пояса, брюшного пресса и рук.Эспандер помогает справиться с проблемой плеч, приводит их в тонус, делает мышцы более эластичными и крепкими. Восстановление после травм.Не только профессиональные спортсмены, но и обычные люди используют эспандер, польза которого заключается в укреплении мышц рук. А от физического состояния этой части тела зависит здоровье других систем: сердечно-сосудистой, нервной, иммунной',599),(16,'Штанга изогнутая W образная 32,5 кг','Сильноизогнутая W штанга весом 32,5 кг с быстросъемными фиксаторами\r\nПосадочный диаметр грифа: 25 мм.\r\nМасса грифа с замками: 5 кг.\r\nИспользуются черные диски с прочным резиновым покрытием:\r\n5 кг - 4 шт.\r\n2,5 кг - 2 шт.\r\n1,25 кг - 2 шт.',10632),(17,'Гравийный Велосипед Shulz Wanderer, 29, 2023','Shulz Wanderer - туринговый велосипед с хроммолибденовой рамой, на котором вы сможете не только ежедневно ездить по городу и участвовать в воскресных велопоходах, но и отправиться в серьезное велопутешествие с сумкой-штанами или даже поехать на гонку по циклокроссу. Создан для длинных дистанций и незабываемых впечатлений!',57500),(18,'Беговые лыжи Madshus Nordseter Skate','Спортивные коньковые беговые лыжи с сотовым сердечником для любителей активного катания.\r\nЛЕГКОСТЬ\r\nСотовый сердечник Cell Core гарантирует низкий вес и жесткость лыжи.\r\nСКОРОСТЬ\r\nГоночная скользящая поверхность для максимальной скорости.\r\nУПРАВЛЯЕМОСТЬ\r\nСтреловидная спортивная геометрия 41–44–44 для отличной управляемости.\r\nСКОЛЬЖЕНИЕ\r\nОбработка скользящей поверхности Race Tuning для лучшего скольжения.\r\nДОЛГОВЕЧНОСТЬ\r\nЛаковое покрытие LС Protect для защиты от снега и влаги.\r\nНАДЕЖНОСТЬ\r\nКонструкция CAP обеспечивает прочность лыж.\r\nКАЧЕСТВО\r\nЛегендарный норвежский бренд, производящий экипировку для беговых лыж с 1906 года.',6999);

